// Minimal plugin shim for Chart.js datalabels so chart code doesn't crash if plugin missing
(function(){
  if (!window.Chart || !window.Chart.register) { console.warn('Chart not present; datalabels shim skipped'); return; }
  var plugin = {
    id: 'datalabels',
    beforeDatasetsDraw: function(){}
  };
  try { window.Chart.register(plugin); } catch(e){ console.warn('Could not register datalabels plugin', e); }
})();