(function(){
  // Minimal SweetAlert2 shim for simple alerts used by app.
  function SwalFactory(opts){
    return new Promise(function(resolve){
      var title = (opts && opts.title) ? opts.title : '';
      var text = (opts && opts.text) ? opts.text : '';
      alert((title?title+'\n':'')+text);
      resolve({ isConfirmed: true });
    });
  }
  window.Swal = function(arg1, arg2, arg3){
    if (typeof arg1 === 'string') return SwalFactory({ title: arg1, text: arg2 });
    return SwalFactory(arg1 || {});
  };
  window.Swal.fire = window.Swal;
})();